import React, { useEffect, useState } from 'react';

const Introduction = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Trigger animations on component mount
    setIsVisible(true);
  }, []);

  return (
    <div className="min-h-screen color1">
      <div className="container mx-auto px-4">
        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 pt-24">
          {/* Left Column - Text Content */}
          <div className="font1 relative">
            <div className="pt-16">
              <p 
                className={`text-left fontcolor1 fontsize1 zoom-out-right ${isVisible ? 'animate-in' : ''}`}
                style={{ 
                  color: '#9c4819',
                  fontSize: 'clamp(2rem, 8vw, 3rem)',
                  transitionDelay: '0.2s'
                }}
              >
                Hello,
              </p>
              <p 
                className={`text-left fontsize2 zoom-out-right ${isVisible ? 'animate-in' : ''}`}
                style={{ 
                  fontSize: 'clamp(2.5rem, 10vw, 4rem)',
                  transitionDelay: '0.4s'
                }}
              >
                <b>I'm Rainn</b>
              </p>
              <p 
                className={`text-left fontsize2 zoom-out-right ${isVisible ? 'animate-in' : ''}`}
                style={{ 
                  fontSize: 'clamp(2.5rem, 10vw, 4rem)',
                  transitionDelay: '0.6s'
                }}
              >
                <b>Abrogena!</b>
              </p>
              <p 
                className={`zoom-out-right ${isVisible ? 'animate-in' : ''}`}
                style={{ 
                  transitionDelay: '0.8s',
                  fontSize: '1.1rem',
                  lineHeight: '1.6'
                }}
              >
                A multimedia arts student at Mapua University. She is an aspiring digital illustrator committed to her creative crafts.
              </p>
            </div>
            
            {/* Sideways Text */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Introduction;
